OPERATOR tank;

%Pressure
FOR ALL COMPONENT, rho,c_p,c,Volume,Energy
LET tank(COMPONENT, rho,c_p,c,effort,1,
	Volume,state,1,
	Energy,state,2
	) = (Volume*rho)/c;

%Temperature
FOR ALL COMPONENT, rho,c_p,c,Volume,Energy
LET tank(COMPONENT, rho,c_p,c,effort,2,
	Volume,state,1,
	Energy,state,2
	) = Energy/(Volume*rho*c_p);

END;
